import React, { Component } from 'react';
import axios from 'axios';

export default class Create extends Component {
  constructor(props) {
    super(props);
    this.onChangePersonName = this.onChangePersonName.bind(this);
    this.onChangeDateOfJoining = this.onChangeDateOfJoining.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangeMobileNo = this.onChangeMobileNo.bind(this);
    this.onChangeSalaryPerAnnum = this.onChangeSalaryPerAnnum.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      person_name: '',
      date_of_joining: '',
      email:'',
      mobile_no:'',
      salary_per_annum:''
    }
  }
  onChangePersonName(e) {
    this.setState({
      person_name: e.target.value
    });
  }
  onChangeDateOfJoining(e) {
    this.setState({
      date_of_joining: e.target.value
    })  
  }
  onChangeEmail(e) {
    this.setState({
      email: e.target.value
    })
  }
  onChangeMobileNo(e) {
    this.setState({
      mobile_no: e.target.value
    })
  }
  onChangeSalaryPerAnnum(e) {
    this.setState({
      salary_per_annum: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();
    const obj = {
      person_name: this.state.person_name,  
      date_of_joining: this.state.date_of_joining,
      email: this.state.email,
      mobile_no: this.state.mobile_no,
      salary_per_annum: this.state.salary_per_annum
    };
    axios.post('http://localhost:4000/business/add', obj)
        .then(res => console.log(res.data));

    this.setState({
      person_name: '',
      date_of_joining: '',
      email: '',
      mobile_no:'',
      salary_per_annum:''
    })
  }

  render() {
    return (
        <div style={{ marginTop: 10 }}>
            <h3 align="center">Add New Business</h3>
            <form onSubmit={this.onSubmit}>
                <div className="form-group">
                    <label>Person Name:  </label>
                    <input 
                      type="text" 
                      className="form-control" 
                      value={this.state.person_name}
                      onChange={this.onChangePersonName}
                      />
                </div>
                <div className="form-group">
                    <label>date of joining:</label>
                    <input type="date" 
                      className="form-control"
                      value={this.state.date_of_joining}
                      onChange={this.onChangeDateOfJoining}
                      />
                </div>
                <div className="form-group">
                    <label>Email </label>
                    <input type="text" 
                      className="form-control"
                      value={this.state.email}
                      onChange={this.onChangeEmail}
                      />
                </div>
                <div className="form-group">
                    <label>Mobile No </label>
                    <input type="number" 
                      className="form-control"
                      value={this.state.mobile_no}
                      onChange={this.onChangeMobileNo}
                      />
                </div>
                <div className="form-group">
                    <label>Pay Scale </label>
                    <input type="text" 
                      className="form-control"
                      value={this.state.salary_per_annum}
                      onChange={this.onChangeSalaryPerAnnum}
                      />
                </div>
                <div className="form-group">
                    <input type="submit" 
                      value="Register Business" 
                      className="btn btn-primary"/>
                </div>
            </form>
        </div>
    )
  }
}
